const readline = require('readline');
const rl = readline.createInterface({
  input: process.stdin,
  output: process.stdout
});

rl.on('line', (line) => {
  const n = parseInt(line);
  if (n <= 1) {
    console.log('Not Prime');
  } else {
    let isPrime = true;
    for (let i = 2; i <= Math.sqrt(n); i++) {
      if (n % i === 0) {
        isPrime = false;
        break;
      }
    }
    console.log(isPrime ? 'Prime' : 'Not Prime');
  }
  rl.close();
});

const readline = require('readline');
